INSERT INTO businesses (
    profile_id,
    name,
    phone,
    email,
    website,
    address,
    suburb_id,
    bio,
    pricing,
    abn_verified,
    verification_status,
    resource_type,
    emergency_hours,
    emergency_phone,
    emergency_services,
    cost_indicator,
    capacity_notes,
    is_claimed,
    is_scaffolded,
    is_active
)
SELECT
    NULL,
    r.name,
    r.phone,
    r.email,
    r.website,
    r.address,
    s.id,
    r.bio,
    NULL,
    FALSE,
    'verified',
    r.resource_type::resource_type,
    r.emergency_hours,
    r.emergency_phone,
    ARRAY(SELECT UNNEST(r.emergency_services::text[])),
    r.cost_indicator,
    r.capacity_notes,
    FALSE,
    FALSE,
    TRUE
FROM resources r
JOIN suburbs s ON s.name = r.suburb_name AND s.postcode = r.postcode
LEFT JOIN businesses b ON b.name = r.name AND b.resource_type = r.resource_type::resource_type
WHERE b.id IS NULL;
